import react, { Component } from "react";
import reactDom from "react-dom";
import s from './ImgContainer.module.css'
import './ImgContainer.css';

export default class ImgContainer extends Component {
    constructor(props) {
        super(props);
        // this.fadeInElements();
    }

    hideElements() {
        let container = "";
        if(this.props.id === "textContainer") 
            container = document.getElementById("textContainer");
        else 
            container = document.getElementById("imgContainer");

        container.style.opacity = "0";
        this.fadeInElements(container);
    }

    fadeInElements(container) {
        setInterval(() => {
            container.style.opacity += "0.1";
        }, 100)
    }

    componentDidMount() {
        this.hideElements();
    }

    render() {
        return(
            <div className={s.pageContainer}>
                <div id={this.props.subIdOne} className={s.subDivOne}></div>
                <div id={this.props.id} className={s.subDivTwo}>
                    <h1>{this.props.explainerText}</h1>
                </div>
                <div id={this.props.subIdThree} className={s.subDivThree}></div>
            </div>
        )}
}
