import react, { Component } from "react";
import reactDom from "react-dom";
import s from './ImgContainer.module.css'
import './ImgContainer.css';
import { clear } from "@testing-library/user-event/dist/clear";

export default class ImgContainer extends Component {
    constructor(props) {
        super(props);
    }

    hideElements() {
        let container = "";
        if(this.props.id === "textContainer") 
            container = document.getElementById("textContainer");
        else 
            container = document.getElementById("imgContainer");

        container.style.opacity = "0";
        this.fadeInElements(container);
    }

    fadeInElements(container) {
        let int = setInterval(() => {
            container.style.opacity = parseFloat(container.style.opacity) + 0.01;
            if(container.style.opacity === 1)
                clearInterval(int);
        }, 15)
    }

    componentDidMount() {
        this.hideElements();
    }

    render() {
        return(
            <div className={s.pageContainer}>
                <div className={s.subDivOne}></div>
                <div className={s.subDivTwo}>
                    <div id="textContainer">
                        <h1>Welcome to my portfolio site!</h1>
                    </div>
                </div>
                <div id={this.props.subIdThree} className={s.subDivThree}></div>
            </div>
        )}
}
